import styled, { createGlobalStyle }  from 'styled-components';
import Board from './Board';


export default function Game() {
  return (
    <>
          <GlobalStyle backgroundColor />
          <RootContainer>
            <GameContainer>
              <TitleContainer>Tic-Tac-Toe Game </TitleContainer> 
              <ScoreBoard scores={scores} xPlaying={xPlaying}/>
              <BoardPage board={board} onClick={gameOver ? resetBoard : handleSquareClick}/>
              <ResetButton resetBoard={resetBoard}/>
            </GameContainer>
          </RootContainer>
        </>
  )
}

//styled componenents
const GlobalStyle = createGlobalStyle`
  body {
    background-color: ${props => (props.backgroundColor ? '#334257' : 'black')};
  }
  `

const RootContainer = styled.div`
  font-family: "museo", Century Gothic, Helvetica, sans-serif;
  margin:0;
  padding:0;
`

const GameContainer = styled.h1`
  display: flex;
  flex-direction: column;
  align-items: center;
`

const TitleContainer = styled.h1`
  color: #F94C66;
`
